package com.heapspace.solutions.leetcode;

import java.util.Arrays;

public class StringDifference {

	   public char findTheDifference(String s, String t) {
	        char[] sArr = s.toCharArray();
	        char[]tArr = t.toCharArray();
	        Arrays.sort(sArr);
	        Arrays.sort(tArr);
	        for(int i = 0,j=0 ; j < sArr.length && i < tArr.length; j++, i++){
	        	if(tArr[i]!=sArr[j]) {
	        		return tArr[i];
	        	}
	        }
	        return tArr[tArr.length-1];
	    }
	   
	   public static void main(String[] args) {
//		   System.out.println(new StringDifference().findTheDifference(""
//				   , "")
//				   );
		   System.out.println(new StringDifference().findTheDifference("ab", "abb"));
	   }
}
